import React, { useEffect, useState } from "react";
import BotMap from "./BotMap";
import BotSpecs from "./BotSpecs";

const BotCollection = () => {
  //create the state of the current data
  const [data, setData] = useState([]);
  const [selectCard, setselectCard] = useState(null);

  // create a function to pass to its children(clicking to open the card)
  const handleclick = (card) => {
    setselectCard(card);
  };


  //Fetch data
  const fetchData = () => {
    fetch("http://localhost:3000/bots")
      .then((res) => res.json())
      .then((data) => setData(data))
      .catch((error) => console.log("Error fetching", error));
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div>
      {selectCard ? (
      <BotSpecs card={selectCard} style={{backgroundColor:"yellow"}}/>
      ): ( 
      <BotMap data={data} onCardClick={handleclick}/>
      )};
    </div>
  );
};

export default BotCollection;
