import React, { useState } from "react";
import BotCard from "./BotCard";
import BotSpecs from "./BotSpecs";
const BotMap = ({ data }) => {

  const [selectCard, setselectCard] = useState(null);
  const [showDetails, setshowDetails] = useState(false);

  //function that handles the cardclick
  const handleClick = (card) => {
    setselectCard(card);
    setshowDetails(true);
  };

  //function to handle bot close
  const handleClose = () => {
    setselectCard(null);
    setshowDetails(false);
  };
  return (
    //  map through the data we have fetched
    <div style={{ marginTop: "150px" }}>
      {data.map((item) => (
        <BotCard
          key={item.id}
          image={item.avatar_url}
          name={item.name}
          phrase={item.catchphrase}
          health={item.health}
          damage={item.damage}
          armor={item.armor}
          onClick={() => handleClick(item)} //pass bot data when i click
        />
      ))}
      {/* Render BotSpecs if the showDetails is true and a botcard has been selected */}
      {showDetails && selectCard && (
        <BotSpecs card={selectCard} onClose={handleClose} />
      )}
    </div>
  );
};

export default BotMap;
